import React from "react";
import './dailychart.css'
import { Bar } from "react-chartjs-2";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
} from "chart.js";

// Chart.js에 필요한 요소 등록
ChartJS.register(CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend);

const DailyChart = ({ data }) => {
  const groupedData = [];
  const groupSize = 5; // 5일 단위로 그룹화

  for (let i = 0; i < data.length; i += groupSize) {
    const group = data.slice(i, i + groupSize); // 5일씩 묶기
    const groupLabel = `${group[0].date} ~ ${group[group.length - 1].date}`; // 그룹의 시작~끝 날짜 라벨
    const groupValue = group.reduce((sum, item) => sum + item.ratio, 0); // 그룹의 비율 합계

    groupedData.push({ label: groupLabel, value: groupValue });
  }

  console.log("5일 단위", groupedData)

  const labels = groupedData.map((item) => item.date);
  const values = groupedData.map((item) => item.value);;

  const chartData = {
    labels: labels,
    datasets: [
      {
        label: "일별 검색량",
        data: values,
        backgroundColor: "rgba(7, 7, 7, 1)",
        borderColor: "rgba(7, 7, 7, 1)",
        borderWidth: 1,
        hoverOffset: 4,
      }
    ]
  };

  const options = {
    responsive: true,
    plugins: {
      legend: {
        display: false,
      },
      tooltip: {
        enabled: true, // 툴팁 활성화
        callbacks: {
          label: function (context) {
            const value = context.raw;
            return `총 검색량: ${value.toFixed(2)}%`;
          }
        }
      }   
    },
    scales: {
      x: {
        title: {
          display: true,
          text: "기간",
        },
        grid: {
          display: false,
        },
        ticks: {
          color: "#333",
        },
      },
      y: {
        title: {
          display: true,
          text: "검색량 합계",
        },
        beginAtZero: true,
        grid: {
          display: true,
          color: "rgba(200, 200, 200, 0.2)",
        },
        ticks: {
          color: "#333",
        }
      }
    }
  };


  return (
    <div className="daliy-container">
        <Bar data={chartData} options={options} />
    </div>
  );
};

export default DailyChart;
