import React, { useMemo } from "react";
import './dailychart.css'
import { Bar } from "react-chartjs-2";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
} from "chart.js";

// Chart.js에 필요한 요소 등록
ChartJS.register(CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend);

const DailyChart = ({ data }) => {
  // 전체 검색량 합계 계산
  const totalSearchVolume = useMemo(() => {
    return data.reduce((sum, item) => sum + item.ratio, 0);
  }, [data]);

  // 데이터 5일 단위로 묶기 및 비율 계산
  const groupedData = useMemo(() => {
    const groupSize = 5; // 5일 단위
    const result = [];

    for (let i = 0; i < data.length; i += groupSize) {
      const group = data.slice(i, i + groupSize);
      const groupLabel = `${group[0].date} ~ ${group[group.length - 1].date}`;
      const groupValue = group.reduce((sum, item) => sum + item.ratio, 0);
      const groupRatio = (groupValue / totalSearchVolume) * 100; // 비율 계산
      result.push({ label: groupLabel, value: groupRatio });
    }

    return result;
  }, [data, totalSearchVolume]);

  // 차트에 사용할 데이터
  const labels = groupedData.map((item) => item.label);
  const values = groupedData.map((item) => item.value);

  const chartData = {
    labels: labels,
    datasets: [
      {
        label: "일별 검색량",
        data: values,
        backgroundColor: "rgba(7, 7, 7, 1)",
        borderColor: "rgba(7, 7, 7, 1)",
        borderWidth: 1,
        hoverOffset: 4,
      }
    ]
  };

  const options = {
    responsive: true,
    plugins: {
      legend: {
        display: false,
      },
      tooltip: {
        enabled: true, // 툴팁 활성화
        callbacks: {
          label: function (context) {
            const value = context.raw;
            return `비율: ${value.toFixed(2)}%`;
          }
        }
      }   
    },
    scales: {
      x: {
        title: {
          display: true,
        },
        grid: {
          display: false,
        },
        ticks: {
          color: "#333",
        },
      },
      y: {
        title: {
          display: true,
          text: "검색량 합계",
        },
        beginAtZero: true,
        grid: {
          display: true,
          color: "rgba(200, 200, 200, 0.2)",
        },
        ticks: {
          color: "#333",
        }
      }
    }
  };


  return (
    <div className="daliy-container">
        <Bar data={chartData} options={options} />
    </div>
  );
};

export default DailyChart;
