import React, { useEffect, useState } from "react";
import axios from "axios";
import './featuredInfo.css'

import DailyChart from "../chart/Dailychart";
import MonthlyChart from "../chart/Monthlychart";
// import GenderChart from "../chart/Genderchart";
// import AgesChart from "../chart/Ageschart";


 const FeaturedInfo = ({ keyword }) => {
    const [dailyData, setDailyData] = useState([]);
    const [monthlyData, setMonthlyData] = useState([]);
    // const [genderData, setGenderData] = useState(null);
    // const [agesData, setAgesData] = useState([]);
    const [error, setError] = useState(null);

    // 현재 날짜 기준 요청 데이터 생성
    const calculateDateRange = (timeUnit) => {
        const now = new Date();
        const year = now.getFullYear();
        const month = String(now.getMonth() + 1).padStart(2, "0");
        const day = String(now.getDate()).padStart(2, "0");

        if (timeUnit === "month") {
            // 월별 데이터: 해당 연도의 1월 1일부터 현재 날짜까지
            const startDate = `${year}-01-01`; // 1월 1일
            const endDate = `${year}-${month}-${day}`; // 오늘 날짜
            return { startDate, endDate };
        } else if (timeUnit === "date") {
            // 일별 데이터: 해당 월의 1일부터 현재 날짜까지
            const startDate = `${year}-${month}-01`; // 이번 달의 1일
            const endDate = `${year}-${month}-${day}`; // 오늘 날짜
            return { startDate, endDate };
        }

        // 기본값: 전체 연도 기준
        const startDate = `${year}-01-01`;
        const endDate = `${year}-${month}-${day}`;
        return { startDate, endDate };

    };

    const fetchTrendData = async (timeUnit, type, value) => {
        if (!keyword || keyword.trim() === "") {
            console.error("유효하지 않은 검색어:", keyword);
            setError("검색어를 입력하세요.");
            return null; // 빈 배열 반환
        }

        const { startDate, endDate } = calculateDateRange(timeUnit);
        const requestData = {
            startDate,
            endDate,
            timeUnit,
            keywordGroups: [{ groupName: "trend", keywords: [keyword] }],
        };


        // // 성별/연령대별 데이터를 요청하도록 requestData 확장
        // if (type === "gender") {
        //     requestData.keywordGroups[0].gender = value; // "f" 또는 "m"
        //     // console.log("성별 요청 데이터: ", requestData); 확인됨
        // }

        // if (type === "age") {
        //     requestData.keywordGroups[0].ages = [value]; // "1" ~ "11"
        //     // console.log("연령별 요청 데이터: ", requestData); 확인됨
        // }


        try {
            const response = await axios.post("/api/trend", requestData, {
                headers: { "Content-Type": "application/json" }
            });

            // console.log("API 응답 데이터:", response.data);

            return response.data?.results || []; // API 응답 결과 반환

        } catch (err) {
            console.error("데이터 요청 실패:", err);
            return [];
        }
    };


    // 데이터 요청 및 처리
    useEffect(() => {
        const fetchData = async () => {
            try {
                // 일별 데이터 요청
                const dailyResponse = await fetchTrendData("date");
                if (dailyResponse) {
                    const transformedData = dailyResponse[0]?.data.map((item) => ({
                        date: item.period,
                        ratio: item.ratio,
                    }));
                    setDailyData(transformedData);
                    // console.log("일별 데이터 업데이트:", dailyResponse);
                    // setDailyData(dailyResponse);
                } 

                // 월별 데이터 요청
                const monthlyResponse = await fetchTrendData("month");
                // console.log("월별 데이터 응답:", monthlyResponse); // 구조 확인

                if (monthlyResponse) {
                    const transformedMonthlyData = monthlyResponse[0]?.data.map((item) => ({
                        month: item.period, 
                        ratio: item.ratio, 
                      }));
                      setMonthlyData(transformedMonthlyData);
                    // console.log("월별 데이터 업데이트:", monthlyResponse);
                    // setMonthlyData(monthlyResponse);
                }

                // // 성별 데이터 요청
                // const maleResponse = await fetchTrendData("date", "gender", "m");
                // const femaleResponse = await fetchTrendData("date", "gender", "f");


                // // 남성과 여성 검색량 합계 계산 후 비율 계산
                // const maleSum = maleResponse[0]?.data.reduce((sum, curr) => sum + parseFloat(curr.ratio || 0), 0);
                // const femaleSum = femaleResponse[0]?.data.reduce((sum, curr) => sum + parseFloat(curr.ratio || 0), 0);
                // const totalGender = maleSum + femaleSum;

                // const genderData = {
                //     m: ((maleSum / totalGender) * 100).toFixed(2),
                //     f: ((femaleSum / totalGender) * 100).toFixed(2),
                // };
                // setGenderData(genderData);

                // // 연령별 데이터 요청
                // const ageGroups = {
                //     "10대": ["1", "2"],
                //     "20대": ["3", "4"],
                //     "30대": ["5", "6"],
                //     "40대": ["7", "8"],
                //     "50대": ["9", "10"],
                //     "60대 이상": ["11"],
                // };

                // let totalAgeSum = 0;
                // const ageGroupSums = {};

                // for (const [group, codes] of Object.entries(ageGroups)) {
                //     let groupSum = 0;

                //     for (const code of codes) {
                //         // API 호출 결과 가져오는 부분
                //         const ageResponse = await fetchTrendData("date", "age", code);

                //         // console.log(`연령대 코드 ${code} 응답 데이터:`, ageResponse[0]?.data || []);

                //         const data = ageResponse[0]?.data || [];
                //         groupSum += data.reduce((sum, curr) => sum + parseFloat(curr.ratio || 0), 0);
                //     }

                //     // console.log(`연령대: ${group}, 합계: ${groupSum}`);      // 확인
                //     ageGroupSums[group] = groupSum;
                //     totalAgeSum += groupSum;
                // }
                // // console.log("전체 연령대 합계:", totalAgeSum); // 디버깅: 전체 합계


                // const agesData = {};
                // for (const [group, sum] of Object.entries(ageGroupSums)) {
                //     agesData[group] = ((sum / totalAgeSum) * 100).toFixed(2);
                //     // console.log(`연령대: ${group}, 비율: ${agesData[group]}%`); // 디버깅: 각 연령대 비율
                // }
                // setAgesData(agesData);

                // // console.log("성별 비율:", genderData);
                // // console.log("연령대 비율:", agesData);

            } catch (err) {
                console.error("데이터 요청 실패: ", err);
                setError("데이터 요청에 실패했습니다.");
            }
        };

        fetchData();
    }, [keyword]);

    if (error) {
        return <p>{error}</p>;
    }

    return (
        <div>
            <div className="featured">
                <div className="chartContainer">
                    <span className="topTitle">일별 검색량</span>
                        <DailyChart data={dailyData} />
                </div>

                <div className="pieContainer">
                    <span className="topTitle">성별 검색량</span>
                        {/* <GenderChart data={genderData} /> */}
                </div>

                <div className="pieContainer">
                    <span className="topTitle">연령별 검색량</span>
                        {/* <AgesChart data={agesData} /> */}
                </div>
            </div>

            <div className="lineContainer">
                <span className="lineTitle">월별 검색량</span>
                    <MonthlyChart data={monthlyData}/>
            </div>
        </div>
    );

};


export default FeaturedInfo;
