import React, { useEffect, useState } from "react";
import "./chart.css"
import axios from "axios";
import { Line } from "react-chartjs-2";

const Chart = ({ keyword }) => {
    const [chartData, setChartData] = useState(null);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await axios.get(`/api/trend`, {
                    params: { keyword },
                });

                console.log("서버 응답 데이터: ", response.data);

                // 차트 데이터 가공
                const results = response.data.results[0]; // 첫 번째 결과 그룹 사용
                const labels = response.data.results[0].data.map((item) => item.period);
                const values = response.data.results[0].data.map((item) => item.ratio);

                setChartData({
                    labels,
                    datasets: [
                        {
                            label: `Search Trends for "${keyword}"`,
                            data: values,
                            borderColor: "rgba(75, 192, 192, 1)",
                            borderWidth: 2,
                        },

                    ],
                });
            } catch (error) {
                console.error("데이터 요청 오류: ", error);
            }
        };

        if (keyword) {
            fetchData();
        }
    }, [keyword]);

    return (
        <div>
            {chartData ? (
                <Line 
                  data={chartData}
                  options={{
                    responsive: true,
                    plugins: {
                        legend: { 
                            display: true, 
                            position: "top" 
                        },
                    },
                    scales: {
                        x: {
                            title: {
                                display: true,
                                text: "Period",
                            }
                        }
                    }
                  }}
                />
            ) : (
                <p>Loading chart...</p>
            )}
        </div>
    );
;}

export default Chart;






