import React from "react";
import { Pie } from "react-chartjs-2";
import {
    Chart as ChartJS,
    ArcElement,
    Tooltip,
    Legend,
} from "chart.js";

ChartJS.register(ArcElement, Tooltip, Legend);

const GenderChart = ({ data }) => {

    // 남성 검색 비율 평균 계산
    const maleAverage =
        data.male.reduce((sum, item) => sum + item.ratio, 0) / data.male.length;

    // 여성 검색 비율 평균 계산
    const femaleAverage =
        data.female.reduce((sum, item) => sum + item.ratio, 0) / data.female.length;

    // 차트 데이터
    const chartData = {
        labels: ["남성", "여성"], // 범례 설정
        datasets: [
            {
                data: [maleAverage, femaleAverage], // 평균 비율 데이터
                backgroundColor: ["rgba(54, 162, 235, 0.6)", "rgba(255, 99, 132, 0.6)"], // 남성: 파란색, 여성: 빨간색
                borderColor: ["rgba(54, 162, 235, 1)", "rgba(255, 99, 132, 1)"], // 경계 색상
                borderWidth: 1,
            },
        ],
    };

    const options = {
        responsive: true,
        maintainAspectRatio: false,
        plugins: {
            legend: {
                position: "top",
                labels: {
                    color: "#333", // 범례 색상
                },
            },
            tooltip: {
                callbacks: {
                    label: function (context) {
                        const value = context.raw;
                        return `${context.label}: ${value.toFixed(2)}%`; // 소수점 2자리 표시
                    },
                },
            },
        },
    };

    return <Pie data={chartData} options={options} />;
};

export default GenderChart;
