import React, { useEffect, useState } from "react";
import './featuredInfo.css'
import DailyChart from "../chart/Dailychart";
import MonthlyChart from "../chart/Monthlychart";
import GenderChart from "../chart/Genderchart";
import AgesChart from "../chart/Ageschart";
import axios from "axios";

 const FeaturedInfo = ({ keyword }) => {
    const [dailyData, setDailyData] = useState(null);
    const [monthlyData, setMonthlyData] = useState(null);
    const [genderData, setGenderData] = useState("");
    const [agesData, setAgesData] = useState([]);
    const [error, setError] = useState(null);

    // 현재 날짜 기준 요청 데이터 생성
    const calculateDateRange = (timeUnit) => {
        const now = new Date();
        const year = now.getFullYear();
        const month = String(now.getMonth() + 1).padStart(2, "0");
        const lastDate = new Date(year, now.getMonth() + 1, 0).getDate();

        const startDate = 
            timeUnit === "month" 
                ? `${year}-01-01` 
                : `${year}-${month}-01`;
        const endDate = `${year}-${month}-${lastDate}`;

        return { startDate, endDate };
    };

    const fetchTrendData = async (timeUnit) => {
        if (!keyword || keyword.trim() === "") {
            console.error("유효하지 않은 검색어:", keyword);
            setError("검색어를 입력하세요.");
            return null; // 기본적으로 null 반환
        }

        const { startDate, endDate } = calculateDateRange(timeUnit);
        const requestData = {
            startDate,
            endDate,
            timeUnit,
            keywordGroups: [{ groupName: "trend", keywords: [keyword] }],
        };

        try {
            const response = await axios.post("/api/trend", requestData, {
                headers: {
                    "Content-Type": "application/json"
                }
            });

            return response.data;

        } catch (err) {
            console.error(err);
            setError("데이터 요청에 실패했습니다.");
            return null;
        }
    };

    // 성별 및 연령대 데이터 그룹화 함수
    const groupData = (data) => {
        if (!data || !Array.isArray(data)) {
            console.error("groupData 함수에 전달된 데이터가 유효하지 않습니다:", data);
            return { genderData: { m: 0, f: 0 }, ageGroups: {} };
        }

        const genderData = { m: 0, f: 0 };
        const agesData = {
            "10대": 0,
            "20대": 0,
            "30대": 0,
            "40대": 0,
            "50대": 0,
            "60대 이상": 0,
        };

        data.forEach((entry) => {
            if (entry.gender === "m") {
                genderData.m += entry.value;
            } else if (entry.gender === "f") {
                genderData.f += entry.value;
            }

            entry.ages.forEach((age) => {
                if (["1", "2"].includes(age)) {
                    agesData["10대"] += entry.value;
                } else if (["3", "4"].includes(age)) {
                    agesData["20대"] += entry.value;
                } else if (["5", "6"].includes(age)) {
                    agesData["30대"] += entry.value;
                } else if (["7", "8"].includes(age)) {
                    agesData["40대"] += entry.value;
                } else if (["9", "10"].includes(age)) {
                    agesData["50대"] += entry.value;
                } else if (["11"].includes(age)) {
                    agesData["60대 이상"] += entry.value;
                }
            });
        });

        return { genderData, agesData };
    };


    // 데이터 요청 및 처리
    useEffect(() => {
        const fetchData = async () => {
            try {
                // 일별 데이터 요청
                const dailyResponse = await fetchTrendData("date");
                if (dailyResponse) {
                    setDailyData(dailyResponse.results);
                }

                // 월별 데이터 요청
                const monthlyResponse = await fetchTrendData("month");
                if (monthlyResponse) {
                    setMonthlyData(monthlyResponse.results);

                    // 성별 및 연령 데이터 추출
                    const groupedData = groupData(monthlyResponse.results);
                    setGenderData(groupedData.genderData);
                    setAgesData(groupedData.agesData);
                }
            } catch (err) {
                console.error(err);
                setError("데이터 요청에 실패했습니다.");
            }
        };

        fetchData();
    }, [keyword]);

    if (error) {
        return <p>{error}</p>;
    }

    if (!dailyData || !monthlyData || !genderData || !agesData) {
        return <p>데이터를 로드 중입니다...</p>;
    }

    return (
        <div className="featured">
                <div className="featuredItem">
                    <span className="featuredTitle">일별 검색량</span>
                        <DailyChart data={dailyData} />
                </div>

                <div className="featuredItem">
                    <span className="featuredTitle">성별 검색량</span>
                        <GenderChart data={genderData} />
                </div>

                <div className="featuredItem">
                    <span className="featuredTitle">연령별 검색량</span>
                        <AgesChart data={agesData} />
                </div>
        </div>
    );

};


export default FeaturedInfo;
