import React, { useEffect, useState } from "react";
import './chart.css'
import { LineChart, Line, XAxis, CartesianGrid, Tooltip, ResponsiveContainer } from 'recharts';
import axios from "axios";

function Chart({ keyword }) {
    const [chartData, setChartData] = useState({});

    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await axios.get(`http://localhost:8080/api/trend/monthly/${keyword}`);
                const data = response.data;

                // API 응답 데이터를 그래프 형식으로 가공
                const months = data.map(item => item.month);    // 월 정보
                const searchRates = data.map(item => item.searchRates);     // 검색률 정보

                // Chart.js 데이터 형식으로 설정
                setChartData({
                    labels: months,
                    datasets: [
                        {
                            label: `Monthly Search Rate for ${keyword}`,
                            data: searchRates,
                            fill: false,
                            borderColor: 'blue',
                            tenstion: 0.1
                        }
                    ]
                });
            } catch (error) {
                console.error("Error fetching data:", error);
            }
        };
        fetchData();
    })
}




