import React, { useEffect, useState } from "react";
import "./chart.css"
import axios from "axios";
import { Line } from "react-chartjs-2";
import {
    Chart as ChartJS,
    CategoryScale,
    LinearScale,
    PointElement,
    LineElement,
    Title,
    Tooltip,
    Legend,
} from "chart.js";

ChartJS.register(
    CategoryScale,
    LinearScale,
    PointElement,
    LineElement,
    Title,
    Tooltip,
    Legend
);

const Chart = ({ keyword }) => {
    const [chartData, setChartData] = useState(null);

    useEffect(() => {
        const fetchData = async () => {
            console.log("데이터 요청 시작", keyword);
            try {
                const response = await axios.get(`/api/trend`, {
                    params: { keyword },
                });

                console.log("서버 응답 데이터: ", response.data);

                // 데이터 가공
                const results = response.data.results[0];
                const labels = results.data.map((item) => item.period);
                const values = results.data.map((item) => item.ratio);

                setChartData({
                    labels,
                    datasets: [
                        {
                            label: `"${keyword}" 월별 키워드`,
                            data: values,
                            // borderColor: "rgba(75, 192, 192, 1)",
                            // backgroundColor: "rgba(255, 99, 132, 0.2)",
                            borderWidth: 2,
                            pointBackgroundColor: "rgba(75, 192, 192, 1)", // 포인트 색상
                            pointHoverBackgroundColor: "#ff6384", // 마우스 오버 시 포인트 색상
                        },
                    ],
                });

            } catch (error) {
                console.error("데이터 요청 오류: ", error);
            }
        };

        if (keyword !== "wellness") {
            fetchData();
        }
    }, [keyword]);

    return (
        <div className="chart-container">
            {chartData ? (
                <Line 
                  data={chartData}
                  options={{
                    responsive: true,
                    maintainAspectRatio: false,
                    plugins: {
                        legend: { 
                            display: true, 
                            position: "top", 
                            labels: {
                                color: "#fff",
                            }
                        },
                    },
                    scales: {
                        x: {
                            title: {
                                display: true,
                            },
                            type: "category",
                            ticks: {
                                color: "#fff", 
                                callback: function (value, index) {
                                  const months = [
                                    "Jan.", "Feb.", "Mar.", "Apr.", "May", "Jun.", 
                                    "Jul.", "Aug.", "Sept.", "Oct.", "Nov.", "Dec."
                                  ];
                                  const date = new Date(chartData.labels[index]);
                                  return months[date.getMonth()];
                                },
                            },
                        },
                        y: {
                            title: {
                                display: true,
                            },
                            ticks: {
                                color: "#fff"
                                // min: 0,
                                // max: 100
                            },
                            beginAtZero: true, // y축을 0부터 시작
                        },
                    },
                    // elements: {
                    //     line: {
                    //       borderColor: "rgba(255, 99, 132, 0.5)", // 선 색상
                    //       backgroundColor: "rgba(255, 99, 132, 0.1)", // 배경색
                    //     },
                    //     point: {
                    //       backgroundColor: "rgba(75, 192, 192, 1)", // 포인트 배경색
                    //       hoverBackgroundColor: "#ff6384", // 마우스 오버 포인트 색상
                    //     },
                    // },
                  }}
                />
            ) : (
                <p>Loading chart...</p>
            )}
        </div>
    );
;}

export default Chart;






