import React from "react";
import { Pie } from "react-chartjs-2";
import {
  Chart as ChartJS,
  ArcElement,
  Tooltip,
  Legend,
} from "chart.js";

// Chart.js에 필요한 요소 등록
ChartJS.register(ArcElement, Tooltip, Legend);

const DailyChart = ({ data }) => {


  // 데이터 정렬 및 상위 3개 추출
  const sortedData = [...data].sort((a, b) => b.ratio - a.ratio); // 비율 기준 내림차순 정렬
  const top3Data = sortedData.slice(0, 3); // 상위 3개 추출

  // 순위에 따라 가중치 설정
  const rankWeights = [3, 2, 1]; // 1위: 가중치 3, 2위: 가중치 2, 3위: 가중치 1
  const adjustedRatios = top3Data.map((item, index) => item.ratio * rankWeights[index]);

  const labels = top3Data.map((item) => item.date);
  const ratios = adjustedRatios;

  const chartData = {
    labels: labels,
    datasets: [
      {
        label: "일별 검색량",
        data: ratios,
        backgroundColor: [
          "rgba(75, 192, 192, 0.6)",
          "rgba(255, 99, 132, 0.6)",
          "rgba(54, 162, 235, 0.6)",
        ], 
        borderColor: [
          "rgba(75, 192, 192, 1)",
          "rgba(255, 99, 132, 1)",
          "rgba(54, 162, 235, 1)",
        ],
        borderWidth: 1,
        hoverOffset: 4,
      }
    ]
  };

  const options = {
    responsive: true,
    plugins: {
      legend: {
        display: false,
      },
      tooltip: {
        enabled: true, // 툴팁 활성화
        callbacks: {
          label: function (context) {
            const label = context.label;
            const value = context.raw;
            return `${label}: ${value.toFixed(2)}%`;
          }
        }
      },
      datalabels: {
        color: "#000", // 데이터 라벨 색상
        font: {
          size: 14,
          weight: "bold",
        },
        formatter: (value, context) => {
          const rawValue = context.dataset.data[context.dataIndex];
          return `${context.chart.data.labels[context.dataIndex]} `; // 날짜 표시
        }
      }
    }
  };


  return (
    <div className="chart-container">
        {/* 검색량이 가장 많은 날짜 표시 */}
        <Pie data={chartData} options={options} />
    </div>
  );
};

export default DailyChart;
