import React, { useEffect, useState } from "react";
import axios from "axios";
import { Line } from "react-chartjs-2";
import { useLocation } from "react-router-dom";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
} from "chart.js";

ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend
);

const MonthlyChart = ({ }) => {
    const [chartData, setChartData] = useState(null);
    // const [loading, setLoading] = useState(true);
    const location = useLocation();

    useEffect(() => {
        const fetchMonthlyData = async () => {

            try {
                const response = await axios.post("/api/trend", {
                    params: { keyword, timeUnit: "month" },
                });

                // 데이터 가공
                const results = response.data.results[0];
                const labels = results.data.map((item) => item.period); 
                const values = results.data.map((item) => item.ratio);  

                // 그라데이션 생성
                const canvas = document.createElement("canvas");
                const ctx = canvas.getContext("2d");
                const gradient = ctx.createLinearGradient(0, 0, 0, 400);
                gradient.addColorStop(0, "rgba(204, 234, 119, 0.8)"); 
                gradient.addColorStop(1, "rgba(75, 192, 192, 0)"); 

                setChartData({
                    labels,
                    datasets: [
                        {
                            label: `"${keyword}" 월별 검색량`,
                            data: values,
                            borderColor: gradient,
                            backgroundColor: "rgba(204, 234, 119, 1)",
                            borderWidth: 3,
                            fill: true, 
                            pointRadius: 6, 
                            pointHoverRadius: 6,
                            pointBackgroundColor: "rgba(29, 29, 29, 1)",
                            pointHoverBackgroundColor: "#F5F5F5",
                            tension: 0.4
                          }
                      ]
                  });

                } catch (error) {
                    console.error("월별 데이터 요청 오류:", error);
                } 
            };

            if (keyword !== "wellness") {
                fetchMonthlyData();
            }
    }, [keyword]);

    // 경로 변경 시 차트 데이터 초기화
    useEffect(() => {
        setChartData(null);
        // setLoading(false);
    }, [location.pathname]); 

    return (
        <div className="chart-container">
            <h3 className="chartTitle">by Monthly</h3>
            {chartData ? (
                <Line
                  data={chartData}
                  options={{
                  responsive: true,
                  maintainAspectRatio: false,
                  plugins: {
                      legend: { 
                          display: false, 
                          label: {
                            boxWidth: 0
                          }
                      }
                  },
                  hover: {
                      mode: "nearest", 
                      intersect: true,
                  },
                  interaction: {
                    mode: "index", 
                    intersect: false,
                  },
                  scales: {
                      x: {
                          title: {
                              display: true,
                          },
                          type: "category",
                          grid: {
                              display: true,
                              color: 'rgba(245, 245, 245, 0.2)', 
                              lineWidth: 1
                          },
                          ticks: {
                              color: "#F5F5F5", 
                              callback: function (value, index) {
                                const months = [
                                  "Jan.", "Feb.", "Mar.", "Apr.", "May", "Jun.", 
                                  "Jul.", "Aug.", "Sept.", "Oct.", "Nov.", "Dec."
                                ];
                                const date = new Date(chartData.labels[index]);
                                return months[date.getMonth()];
                              }
                          }
                      },
                      y: {
                          title: {
                              display: true,
                          },
                          grid: {
                              display: true,
                              color: 'rgba(245, 245, 245, 0.2)', 
                              lineWidth: 1
                          },
                          ticks: {
                            color: "#F5F5F5",
                            stepSize: 25
                          },
                          beginAtZero: true,
                      }
                    }
                }}
              />
          ) : (
            <p className="loading-container">Loading chart...</p>
          )}
      </div>
  );
};

export default MonthlyChart;
