import React, { useState } from "react";
import axios from "axios";

import './topbar.css'
import { RiNotification4Line } from "react-icons/ri";
import { FiMoon } from "react-icons/fi";
import imgLogo from "./profile.png";



const Topbar = () => {
    const [ searchTerm, setSearchTerm ] = useState("");

    const handleSearch = async (e) => {
        e.preventDefault();

        console.log("Í≤ÄÏÉâÏñ¥: ", searchTerm) // Ïò§Î•ò ÌôïÏù∏

        // Í≤ÄÏÉâÏñ¥Í∞Ä ÏûÖÎ†•ÎêòÏßÄ ÏïäÏùÄ Í≤ΩÏö∞ Ï≤òÎ¶¨
        if (!searchTerm) {
            alert("Í≤ÄÏÉâÏñ¥Î•º ÏûÖÎ†•ÌïòÏÑ∏Ïöî.");
            return;
        }

        // Í≤ÄÏÉâÏñ¥Í∞Ä ÏûÖÎ†•Îêú Í≤ΩÏö∞ Ï≤òÎ¶¨
        try {
            const response = await axios.get(`http://localhost:8080/api/trend`, {
                param: { keyword: searchTerm }
            });

            console.log("ÏÑúÎ≤ÑÏóêÏÑú Î∞õÏùÄ Îç∞Ïù¥ÌÑ∞: ", response.data);

        } catch (error) {
            console.error("Ïò§Î•ò Î∞úÏÉù: ", error);
        }
    };

    return (
        <div className="topbar">
            <div className="topbarWrapper">
                <div className="searchWrapper">
                    <form onSubmit={handleSearch}>
                    <input 
                        type="search" 
                        placeholder="Search" 
                        value={searchTerm}
                        onChange={(e) => setSearchTerm(e.target.value)}
                    />
                    <button type="submit">üîç</button>
                    </form>
                </div>
                <div className="topIconWrapper">
                    {/* topbarIcon */}
                    <div className="topbarIconContainer">
                        <FiMoon />
                    </div>
                    {/* topbarIcon */}
                    <div className="topbarIconContainer">
                        <RiNotification4Line />
                        <span className="topIconBadge">2</span>
                    </div>
                    <img src={imgLogo} alt="" className="topAvatar" />
                </div>
            </div>
        </div>
        )
    }

export default Topbar;
