import React, { useEffect, useState } from "react";
import './featuredInfo.css'
import DailyChart from "../chart/Dailychart";
import MonthlyChart from "../chart/Monthlychart";
// import GenderChart from "../chart/Genderchart";
// import AgesChart from "../chart/Ageschart";
import axios from "axios";

 const FeaturedInfo = ({ keyword }) => {
    const [dailyData, setDailyData] = useState(null);
    const [monthlyData, setMonthlyData] = useState(null);
    const [genderData, setGenderData] = useState(null);
    const [agesData, setAgesData] = useState([]);
    const [error, setError] = useState(null);

    // 현재 날짜 기준 요청 데이터 생성
    const calculateDateRange = (timeUnit) => {
        const now = new Date();
        const year = now.getFullYear();
        const month = String(now.getMonth() + 1).padStart(2, "0");
        const day = String(now.getDate()).padStart(2, "0");
        const startDate = 
            timeUnit === "month" 
                ? `${year}-01-01` 
                : `${year}-${month}-01`;
        const endDate = `${year}-${month}-${day}`;

        return { startDate, endDate };
    };

    const fetchTrendData = async (timeUnit, type, value) => {
        if (!keyword || keyword.trim() === "") {
            console.error("유효하지 않은 검색어:", keyword);
            setError("검색어를 입력하세요.");
            return null; // 빈 배열 반환
        }

        const { startDate, endDate } = calculateDateRange(timeUnit);
        const requestData = {
            startDate,
            endDate,
            timeUnit,
            keywordGroups: [{ groupName: "trend", keywords: [keyword] }],
        };


        // 성별/연령대별 데이터를 요청하도록 requestData 확장
        if (type === "gender") {
            requestData.keywordGroups[0].gender = value; // "f" 또는 "m"
        } else if (type === "age") {
            requestData.keywordGroups[0].ages = [value]; // "1" ~ "11"
        }


        try {
            const response = await axios.post("/api/trend", requestData, {
                headers: { "Content-Type": "application/json" }
            });

            return response.data?.results || []; // API 응답 결과 반환

            // console.log("API 응답 데이터:", response.data);

            // if (response.data || response.data.results) {
            //     return response.data.results;
            // } else {
            //     console.error("API 응답 결과가 없습니다: ", response.data);
            //     return [];
            // }
        } catch (err) {
            console.error("데이터 요청 실패:", err);
            return [];
        }


    };


    // 데이터 요청 및 처리
    useEffect(() => {
        const fetchData = async () => {
            try {
                // 일별 데이터 요청
                const dailyResponse = await fetchTrendData("date");
                if (dailyResponse) {
                    // console.log("일별 데이터 업데이트:", dailyResponse);
                    setDailyData(dailyResponse);
                } 

                // 월별 데이터 요청
                const monthlyResponse = await fetchTrendData("month");
                if (monthlyResponse) {
                    // console.log("월별 데이터 업데이트:", monthlyResponse);
                    setMonthlyData(monthlyResponse);
                }

                // 성별 데이터 요청
                const maleResponse = await fetchTrendData("month", "gender", "m");
                const femaleResponse = await fetchTrendData("month", "gender", "f");


                // 남성과 여성 검색량 합계 계산 후 비율 계산
                const maleSum = maleResponse[0]?.data.reduce((sum, curr) => sum + curr.ratio, 0) || 0;
                const femaleSum = femaleResponse[0]?.data.reduce((sum, curr) => sum + curr.ratio, 0) || 0;
                const totalGender = maleSum + femaleSum;

                    const genderData = {
                        m: (maleSum / totalGender) * 100, 
                        f: (femaleSum / totalGender) * 100, 
                    };

                    setGenderData(genderData);

                // 연령별 데이터 요청
                const ageGroups = {
                    "10대": ["1", "2"],
                    "20대": ["3", "4"],
                    "30대": ["5", "6"],
                    "40대": ["7", "8"],
                    "50대": ["9", "10"],
                    "60대 이상": ["11"],
                };

                let totalAgeSum = 0;
                const ageGroupSums = {};

                for (const [group, codes] of Object.entries(ageGroups)) {
                    let groupSum = 0;
                    for (const code of codes) {
                        const ageResponse = await fetchTrendData("month", "age", code);
                        groupSum += ageResponse[0]?.data.reduce((sum, curr) => sum + curr.ratio, 0) || 0;
                    }
                    ageGroupSums[group] = groupSum;
                    totalAgeSum += groupSum;
                }

                const agePercentages = {};
                for (const [group, sum] of Object.entries(ageGroupSums)) {
                    agePercentages[group] = ((sum / totalAgeSum) * 100).toFixed(2);
                }
                setAgesData(agePercentages);

                console.log("성별 비율:", genderRatio);
                console.log("연령대 비율:", agePercentages);

            } catch (err) {
                console.error("데이터 요청 실패: ", err);
                setError("데이터 요청에 실패했습니다.");
            }
        };

        fetchData();
    }, [keyword]);

    if (error) {
        return <p>{error}</p>;
    }

    if (!dailyData || !monthlyData || !genderData || !agesData ) {
        return <p>데이터를 로드 중입니다...</p>;
    }

    return (
        <div className="featured">
            <div className="top">
                <div className="featuredItem">
                    <span className="featuredTitle">일별 검색량</span>
                        <DailyChart data={dailyData} />
                </div>

                <div className="featuredItem">
                    <span className="featuredTitle">성별 검색량</span>
                        {/* <GenderChart data={genderData} /> */}
                </div>

                <div className="featuredItem">
                    <span className="featuredTitle">연령별 검색량</span>
                        {/* <AgesChart data={agesData} /> */}
                </div>
            </div>
            <div className="middle">
                <h3 className="lineChart">월별 검색량</h3>
                <MonthlyChart cata={monthlyData} />
            </div>
        </div>
    );

};


export default FeaturedInfo;
