import React from "react";
import { Pie } from "react-chartjs-2";
import {
  Chart as ChartJS,
  ArcElement,
  Tooltip,
  Legend,
} from "chart.js";

// Chart.js에 필요한 요소 등록
ChartJS.register(ArcElement, Tooltip, Legend);

const DailyChart = ({ data }) => {



  if (!data) {
    return <p>Loading daily chart...</p>;
  }


  // 데이터 형식 확인 및 변환 (예: [{date: "2024-11-01", ratio: 30}, {date: "2024-11-02", ratio: 70}])
  const labels = data.map((item) => item.date);
  const ratios = data.map((item) => item.ratio);

  // // 검색량이 가장 높은 날짜 계산
  // const maxSearchDay = data.reduce((max, entry) =>
  //   entry.value > max.value ? entry : max
  // );

  // 차트 데이터 구성
  const chartData = {
    labels: labels,
    datasets: [
      {
        label: "일별 검색량",
        data: ratios,
        backgroundColor: [
          "rgba(75, 192, 192, 0.2)", // 파이차트 색상
          "rgba(255, 99, 132, 0.2)",
          "rgba(54, 162, 235, 0.2)",
          "rgba(255, 206, 86, 0.2)",
          "rgba(153, 102, 255, 0.2)",
          "rgba(255, 159, 64, 0.2)",
        ], 
        borderColor: [
          "rgba(75, 192, 192, 1)",
          "rgba(255, 99, 132, 1)",
          "rgba(54, 162, 235, 1)",
          "rgba(255, 206, 86, 1)",
          "rgba(153, 102, 255, 1)",
          "rgba(255, 159, 64, 1)",
        ],
        borderWidth: 1,
        hoverOffset: 4,
      }
    ]
  };

  const options = {
    responsive: true,
    plugins: {
      legend: {
        position: "bottom", // 범례 위치
      },
      tooltip: {
        enabled: true, // 툴팁 활성화
      }
    }
  };


  return (
    <div>
      <h4>일별 검색량</h4>
        {/* 검색량이 가장 많은 날짜 표시 */}
        <Pie data={chartData} options={options} />
    </div>
  );
};

export default DailyChart;
