import React from "react";
import './chart.css';
import { Line } from "react-chartjs-2";
import axios from "axios";

const Chart = ({ data, keyword }) => {

    // data로부터 차트에 표시할 값들을 추출
    const months = data.map(item => item.month);    
    const searchRates = data.map(item => item.searchRates);    

    // Chart.js 데이터 형식으로 설정
    const chartData = {
            labels: months,
            datasets: [
                {
                    label: `${keyword}의 월별 검색률`,
                    data: searchRates,
                    fill: false,
                    borderColor: 'blue',
                    tenstion: 0.1
                },
            ],
    };
    return (
        <div>
            <h2>{keyword} Monthly Search Rate</h2>
            {chartData.labels.length > 0 ? (
                <Line data={chartData} />
            ) : (
                <p> Loading... </p>
            )}

        </div>
    );
}

export default Chart();





