import React, { useEffect, useState } from "react";
import './dashboard.css'
import axios from "axios";
// import { userData } from '../../dummyData' 
import Chart from "../../components/chart/Chart";
import FeaturdInfo from "../../components/featuredInfo/FeaturedInfo";
import WidgetLg from "../../components/widgets/widgetLg/WidgetLg";
import WidgetSm from "../../components/widgets/widgetSm/WidgetSm";
import WidgetCol from "../../components/widgets/widgetCol/WidgetCol";
import { useLocation } from "react-router-dom";

const Dashboard = () => {
    const location = useLocation();
    const params = new URLSearchParams(location.search);
    const keyword = params.get("keyword");
    const [chartData, setChartData] = useState(null);

    useEffect(() => {
        if (keyword) {
            loadChartData(keyword);
        }
    }, [keyword]);

    const loadChartData = async (keyword) => {
        try {
            const response = await axios.get(`http://localhost:8080/api/dashboard?keyword=${keyword}`);
            setChartData(response.data);
        } catch (error) {
            console.log("Error: ", error);
        }
    }

    return (
        <div className="dashboard">
            <div className="Widgets">
                <FeaturdInfo />
                    <Chart 
                        data={chartData}
                        title="월별 검색량"
                        grid
                        dataKey="Active User"
                    />
            </div>
            <div className="row">
                <WidgetSm />
                <WidgetLg />
            </div>
            <div className="col">
                <WidgetCol />
            </div>
        </div>
    )
}

export default Dashboard();
