import React, { useEffect, useState } from "react";
import "./chart.css"
import axios from "axios";
import { Line } from "react-chartjs-2";
import {
    Chart as ChartJS,
    CategoryScale,
    LinearScale,
    PointElement,
    LineElement,
    Title,
    Tooltip,
    Legend,
} from "chart.js";

ChartJS.register(
    CategoryScale,
    LinearScale,
    PointElement,
    LineElement,
    Title,
    Tooltip,
    Legend
);

const Chart = ({ keyword }) => {
    const [chartData, setChartData] = useState(null);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await axios.get(`/api/trend`, {
                    params: { keyword },
                });

                console.log("서버 응답 데이터: ", response.data);

                // 차트 데이터 가공
                const results = response.data.results[0]; // 첫 번째 결과 그룹 사용
                const labels = results.data.map((item) => item.period);
                const values = results.data.map((item) => item.ratio);

                // // 데이터 확인
                // console.log("Labels: ", labels);
                // console.log("Values: ", values);

                setChartData({
                    labels,
                    datasets: [
                        {
                            label: `Search Trends for "${keyword}"`,
                            data: values,
                            borderColor: "rgba(75, 192, 192, 1)",
                            borderWidth: 2,
                            pointBackgroundColor: "rgba(75, 192, 192, 1)", // 포인트 색상
                            pointHoverBackgroundColor: "#ff6384", // 마우스 오버 시 포인트 색상
                        },
                    ],
                });

                // console.log("Chart Data: ", chartData);

            } catch (error) {
                console.error("데이터 요청 오류: ", error);
            }
        };

        if (keywrod)

        fetchData();
    }, [keyword]);

    return (
        <div>
            {chartData ? (
                <Line 
                  data={chartData}
                  options={{
                    responsive: true,
                    maintainAspectRatio: false,
                    plugins: {
                        legend: { 
                            display: true, 
                            position: "top" 
                        },
                    },
                    scales: {
                        x: {
                            title: {
                                display: true,
                                text: "Period",
                            },
                            type: "category",
                        },
                        y: {
                            title: {
                                display: true,
                                text: "Search Ratio",
                            },
                        },
                    },
                  }}
                />
            ) : (
                <p>Loading chart...</p>
            )}
        </div>
    );
;}

export default Chart;






